document.addEventListener("DOMContentLoaded",function(){let a=document.querySelectorAll(".tab"),c=document.querySelectorAll(".tabs__button");c.forEach(e=>{e.addEventListener("click",function(){c.forEach(t=>{t.classList.remove("active"),t.setAttribute("aria-selected","false")}),a.forEach(t=>t.classList.remove("active")),e.classList.add("active"),e.setAttribute("aria-selected","true");var t=e.getAttribute("data-tab");document.getElementById(t).classList.add("active")})})});